/*Table structure for table `tb_processos_disciplinar` */

DROP TABLE IF EXISTS `tb_processos_disciplinar`;

CREATE TABLE `tb_processos_disciplinar` (
  `Codigo` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `Descricao` varchar(45) NOT NULL,
  `Codigo_Matricula` int(10) unsigned NOT NULL COMMENT 'Campo que grava os que ja esta confirmado',
  `Data_Proc_Disc` date NOT NULL,
  `Codigo_Utilizador` int(10) unsigned NOT NULL,
  PRIMARY KEY (`Codigo`),
  KEY `FK_tb_processos_disciplinar_Matricula` (`Codigo_Matricula`),
  KEY `FK_tb_processos_disciplinar_Utilizador` (`Codigo_Utilizador`),
  CONSTRAINT `FK_tb_processos_disciplinar_matricula` FOREIGN KEY (`Codigo_Matricula`) REFERENCES `tb_matricula` (`Codigo`),
  CONSTRAINT `FK_tb_processos_disciplinar_utilizador` FOREIGN KEY (`Codigo_Utilizador`) REFERENCES `tb_utilizador` (`Codigo`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

